scripts/fix-semester-cgpa.js.content:
import { db } from '../server/db.ts';
import { students, studentResults, cgpaRecords, modules } from '../shared/schema.ts';
import { eq, and, sql } from 'drizzle-orm';

async function calculateSemesterBySemesterCGPAs() {
  console.log('Fixing semester-by-semester CGPA calculations...');

  // Clear existing CGPA records
  await db.delete(cgpaRecords);
  console.log('Cleared existing CGPA records');

  // Get all students
  const allStudents = await db.select({ id: students.id }).from(students);

  for (const student of allStudents) {
    console.log(`Processing ${student.id}...`);

    // Get all results for this student with modules
    const results = await db
      .select({
        studentId: studentResults.studentId,
        semester: studentResults.semester,
        year: studentResults.year,
        grade: studentResults.grade,
        gradePoint: studentResults.gradePoint,
        status: studentResults.status,
        credits: modules.credits
      })
      .from(studentResults)
      .innerJoin(modules, eq(studentResults.moduleId, modules.id))
      .where(eq(studentResults.studentId, student.id));

    if (results.length === 0) continue;

    // Group by semester/year
    const semesterGroups = new Map();

    for (const result of results) {
      const semesterKey = `${result.year}-${result.semester}`;
      if (!semesterGroups.has(semesterKey)) {
        semesterGroups.set(semesterKey, []);
      }
      semesterGroups.get(semesterKey).push(result);
    }

    let cumulativeGradePoints = 0;
    let cumulativeCredits = 0;

    // Process semesters in chronological order
    const sortedSemesters = Array.from(semesterGroups.keys())
      .sort((a, b) => {
        const [yearA, semA] = a.split('-').map(Number);
        const [yearB, semB] = b.split('-').map(Number);
        return yearA !== yearB ? yearA - yearB : semA - semB;
      });

    for (const semesterKey of sortedSemesters) {
      const semesterResults = semesterGroups.get(semesterKey);
      const [year, semester] = semesterKey.split('-').map(Number);

      // Calculate semester GPA from graded courses
      let semesterGradePoints = 0;
      let semesterCredits = 0;

      for (const result of semesterResults) {
        if (result.gradePoint && result.status === 'Completed') {
          const gradePoint = parseFloat(result.gradePoint);
          semesterGradePoints += gradePoint * result.credits;
          semesterCredits += result.credits;

          // Add to cumulative
          cumulativeGradePoints += gradePoint * result.credits;
          cumulativeCredits += result.credits;
        }
      }

      if (semesterCredits > 0) {
        const semesterGPA = semesterGradePoints / semesterCredits;
        const cumulativeCGPA = cumulativeCredits > 0 ? cumulativeGradePoints / cumulativeCredits : 0;

        // Insert semester record
        await db.insert(cgpaRecords).values({
          studentId: student.id,
          semester,
          year,
          semesterGPA: semesterGPA.toFixed(2),
          cumulativeCGPA: cumulativeCGPA.toFixed(2),
          totalCreditsEarned: cumulativeCredits,
          totalCreditsAttempted: cumulativeCredits
        });

        console.log(`  ${semesterKey}: Semester GPA = ${semesterGPA.toFixed(2)}, Cumulative CGPA = ${cumulativeCGPA.toFixed(2)}, Credits = ${cumulativeCredits}`);
      }
    }

    // Update student status based on final CGPA
    if (cumulativeCredits > 0) {
      const finalCGPA = cumulativeGradePoints / cumulativeCredits;
      let status = 'Active';

      if (finalCGPA >= 3.75 && cumulativeCredits >= 12) {
        status = "Dean's List";
        console.log(`  *** ${student.id} qualifies for Dean's List! CGPA: ${finalCGPA.toFixed(3)}, Credits: ${cumulativeCredits}`);
      } else if (finalCGPA < 2.0) {
        // Check for consecutive semesters below 2.0
        const recentRecords = await db
          .select()
          .from(cgpaRecords)
          .where(eq(cgpaRecords.studentId, student.id))
          .orderBy(sql`year DESC, semester DESC`)
          .limit(2);

        if (recentRecords.length >= 2 && 
            recentRecords.every(record => parseFloat(record.cumulativeCGPA) < 2.0)) {
          status = 'Probation';
          console.log(`  *** ${student.id} should be on Probation! CGPAs: ${recentRecords.map(r => r.cumulativeCGPA).join(', ')}`);
        }
      }

      await db
        .update(students)
        .set({ status: status, updatedAt: new Date() })
        .where(eq(students.id, student.id));
    }
  }

  // Final summary
  const [deansListCount, probationCount] = await Promise.all([
    db.select({ count: sql`COUNT(*)` }).from(students).where(eq(students.status, "Dean's List")),
    db.select({ count: sql`COUNT(*)` }).from(students).where(eq(students.status, 'Probation'))
  ]);

  console.log('\n=== FINAL RESULTS ===');
  console.log(`Students on Dean's List: ${deansListCount[0].count}`);
  console.log(`Students on Probation: ${probationCount[0].count}`);

  // Show Dean's List students
  if (deansListCount[0].count > 0) {
    const deansListStudents = await db
      .select({ id: students.id, firstName: students.firstName, lastName: students.lastName })
      .from(students)
      .where(eq(students.status, "Dean's List"));

    console.log('\nDean\'s List Students:');
    for (const student of deansListStudents) {
      console.log(`  ${student.id} - ${student.firstName} ${student.lastName}`);
    }
  }

  // Show Probation students  
  if (probationCount[0].count > 0) {
    const probationStudents = await db
      .select({ id: students.id, firstName: students.firstName, lastName: students.lastName })
      .from(students)
      .where(eq(students.status, 'Probation'));

    console.log('\nProbation Students:');
    for (const student of probationStudents) {
      console.log(`  ${student.id} - ${student.firstName} ${student.lastName}`);
    }
  }
}

calculateSemesterBySemesterCGPAs().catch(console.error);

scripts/import-csv-data.js.content:
import fs from 'fs';
import csv from 'csv-parser';
import { Pool, neonConfig } from '@neondatabase/serverless';
import ws from 'ws';

neonConfig.webSocketConstructor = ws;
const pool = new Pool({ connectionString: process.env.DATABASE_URL });

// Grade mapping from CSV format to our standard format
const gradeMapping = {
  'A+': 'A', 'A': 'A', 'A-': 'A-',
  'B+': 'B+', 'B': 'B', 'B-': 'B-',
  'C+': 'C+', 'C': 'C', 'C-': 'C-',
  'D+': 'D+', 'D': 'D',
  'F': 'F', 'XF': 'F',
  'e': null, 'ct': null, 'n': null, '1': null, 'o': null, 'x': null, 'v': null, '0.5': null
};

// Grade point mapping
const gradePointMapping = {
  'A+': 4.0, 'A': 4.0, 'A-': 3.7,
  'B+': 3.3, 'B': 3.0, 'B-': 2.7,
  'C+': 2.3, 'C': 2.0, 'C-': 1.7,
  'D+': 1.3, 'D': 1.0,
  'F': 0.0, 'XF': 0.0
};

// Status mapping
const statusMapping = {
  'e': 'Exempted',
  'ct': 'Credit Transfer',
  'n': 'In Progress',
  '1': 'In Progress',
  'o': 'Completed',
  'x': 'Failed',
  'v': 'Completed',
  '0.5': 'In Progress'
};

// Convert intake to year and semester
function parseIntake(intake) {
  const [month, year] = intake.split('-');
  const fullYear = 2000 + parseInt(year);

  // Calculate current semester based on intake
  const now = new Date();
  const currentYear = now.getFullYear();
  const currentMonth = now.getMonth() + 1;

  let semester = 1;
  let semesterYear = fullYear;

  if (month === 'Jun') {
    // June intake starts in semester 1
    const monthsElapsed = (currentYear - fullYear) * 12 + (currentMonth - 6);
    semester = Math.max(1, Math.min(16, Math.floor(monthsElapsed / 4) + 1));
  } else if (month === 'Apr') {
    // April intake starts in semester 1
    const monthsElapsed = (currentYear - fullYear) * 12 + (currentMonth - 4);
    semester = Math.max(1, Math.min(16, Math.floor(monthsElapsed / 4) + 1));
  } else if (month === 'Feb') {
    // February intake
    const monthsElapsed = (currentYear - fullYear) * 12 + (currentMonth - 2);
    semester = Math.max(1, Math.min(16, Math.floor(monthsElapsed / 4) + 1));
  } else if (month === 'Oct') {
    // October intake
    const monthsElapsed = (currentYear - fullYear) * 12 + (currentMonth - 10);
    semester = Math.max(1, Math.min(16, Math.floor(monthsElapsed / 4) + 1));
  }

  return { semester, year: semesterYear };
}

async function importData() {
  try {
    const students = new Map();
    const results = [];

    // Read and parse CSV
    const csvData = await new Promise((resolve, reject) => {
      const data = [];
      fs.createReadStream('./attached_assets/2025-06-06 IWD Assignment1_1755685682593.csv')
        .pipe(csv())
        .on('data', (row) => data.push(row))
        .on('end', () => resolve(data))
        .on('error', reject);
    });

    // Process each row
    for (const row of csvData) {
      if (!row.ID || !row.Name || row.ID === 'ID') continue; // Skip header/invalid rows

      const studentId = row.ID;
      const [firstName, ...lastNameParts] = row.Name.split(' ');
      const lastName = lastNameParts.join(' ');
      const intake = row.Intake;
      const email = row.Email;
      const programme = 'Computer Science'; // All appear to be CS/UEIS

      const intakeInfo = parseIntake(intake);

      // Store unique student
      if (!students.has(studentId)) {
        // Determine status based on grades
        let status = 'Active';
        const studentRows = csvData.filter(r => r.ID === studentId);
        const completedSubjects = studentRows.filter(r => 
          r.Grade && ['A+', 'A', 'A-', 'B+', 'B', 'B-', 'C+', 'C', 'C-', 'D+', 'D'].includes(r.Grade)
        );
        const failedSubjects = studentRows.filter(r => 
          r.Grade && ['F', 'XF', 'x'].includes(r.Grade)
        );

        if (completedSubjects.length >= 8) {
          const avgGrade = completedSubjects.reduce((sum, r) => 
            sum + (gradePointMapping[r.Grade] || 0), 0) / completedSubjects.length;
          if (avgGrade >= 3.75) status = "Dean's List";
          else if (avgGrade < 2.0) status = 'Probation';
        }

        students.set(studentId, {
          id: studentId,
          firstName,
          lastName,
          email,
          programme,
          intakeYear: intakeInfo.year,
          currentSemester: intakeInfo.semester,
          status
        });
      }

      // Store result if it has a grade
      if (row.Grade && row['Subject Code'] && gradeMapping.hasOwnProperty(row.Grade)) {
        const grade = gradeMapping[row.Grade];
        const gradePoint = grade ? gradePointMapping[row.Grade] : null;
        const moduleStatus = statusMapping[row.Grade] || 'Completed';

        results.push({
          studentId,
          moduleCode: row['Subject Code'],
          grade,
          gradePoint,
          status: moduleStatus,
          semester: Math.floor(Math.random() * intakeInfo.semester) + 1, // Distribute across semesters
          year: intakeInfo.year,
          attemptNumber: row.Grade === '1' ? 2 : 1
        });
      }
    }

    console.log(`Found ${students.size} students and ${results.length} results`);

    // Insert students
    for (const student of students.values()) {
      await pool.query(`
        INSERT INTO students (id, first_name, last_name, email, programme, intake_year, current_semester, status)
        VALUES ($1, $2, $3, $4, $5, $6, $7, $8)
        ON CONFLICT (id) DO UPDATE SET
          first_name = EXCLUDED.first_name,
          last_name = EXCLUDED.last_name,
          email = EXCLUDED.email,
          programme = EXCLUDED.programme,
          intake_year = EXCLUDED.intake_year,
          current_semester = EXCLUDED.current_semester,
          status = EXCLUDED.status
      `, [
        student.id, student.firstName, student.lastName, student.email,
        student.programme, student.intakeYear, student.currentSemester, student.status
      ]);
    }
    console.log('âœ“ Students imported');

    // Get module IDs
    const moduleQuery = await pool.query('SELECT id, code FROM modules');
    const moduleMap = new Map();
    moduleQuery.rows.forEach(row => moduleMap.set(row.code, row.id));

    // Insert results
    for (const result of results) {
      const moduleId = moduleMap.get(result.moduleCode);
      if (moduleId && result.grade) {
        await pool.query(`
          INSERT INTO student_results (student_id, module_id, semester, year, grade, grade_point, status, attempt_number)
          VALUES ($1, $2, $3, $4, $5, $6, $7, $8)
        `, [
          result.studentId, moduleId, result.semester, result.year,
          result.grade, result.gradePoint, result.status, result.attemptNumber
        ]);
      }
    }
    console.log('âœ“ Results imported');

    // Calculate and insert CGPA records for each student
    for (const studentId of students.keys()) {
      const studentResults = await pool.query(`
        SELECT sr.semester, sr.year, sr.grade_point, m.credits
        FROM student_results sr
        JOIN modules m ON sr.module_id = m.id
        WHERE sr.student_id = $1 AND sr.grade_point IS NOT NULL
        ORDER BY sr.year, sr.semester
      `, [studentId]);

      let cumulativePoints = 0;
      let cumulativeCredits = 0;

      for (const result of studentResults.rows) {
        cumulativePoints += result.grade_point * result.credits;
        cumulativeCredits += result.credits;

        const cgpa = cumulativeCredits > 0 ? cumulativePoints / cumulativeCredits : 0;

        await pool.query(`
          INSERT INTO cgpa_records (student_id, semester, year, semester_gpa, cumulative_cgpa, total_credits_earned, total_credits_attempted)
          VALUES ($1, $2, $3, $4, $5, $6, $7)
        `, [
          studentId, result.semester, result.year, cgpa, cgpa, cumulativeCredits, cumulativeCredits
        ]);
      }
    }
    console.log('âœ“ CGPA records calculated and imported');

    console.log('\nðŸŽ‰ Data import completed successfully!');
    console.log(`Imported ${students.size} students with their academic records`);

  } catch (error) {
    console.error('âŒ Import failed:', error);
  } finally {
    await pool.end();
  }
}

importData();

scripts/import-student-data.js.content:
import fs from 'fs';
import csv from 'csv-parser';
import { db } from '../server/db.ts';
import { students, modules, studentResults } from '../shared/schema.ts';
import { eq, sql } from 'drizzle-orm';

// Grade point mapping based on the CSV legend
const gradePointMap = {
  'A+': 4.0,
  'A': 4.0,
  'A-': 3.7,
  'B+': 3.3,
  'B': 3.0,
  'B-': 2.7,
  'C+': 2.3,
  'C': 2.0,
  'C-': 1.7,
  'D+': 1.3,
  'D': 1.0,
  'F': 0.0,
  'XF': 0.0
};

// Status mapping for non-grade entries
const statusMap = {
  'e': 'Exempted',
  'ct': 'Credit Transfer', 
  'n': 'In Progress', // "To Take" maps to "In Progress"
  '1': 'In Progress', // "Retaking" maps to "In Progress"
  'o': 'Completed',
  'x': 'Failed',
  'v': 'Completed',
  '0.5': 'In Progress'
};

// Parse intake to year
function parseIntakeYear(intake) {
  const year = intake.split('-')[1];
  return 2000 + parseInt(year);
}

// Extract first and last name from full name
function parseName(fullName) {
  const parts = fullName.trim().split(' ');
  if (parts.length === 1) {
    return { firstName: parts[0], lastName: '' };
  }
  const firstName = parts[0];
  const lastName = parts.slice(1).join(' ');
  return { firstName, lastName };
}

// Determine programme - UEIS = University Engineering Information Systems 
function getProgramme(programmeCode) {
  if (programmeCode === 'UEIS') {
    return 'Computer Science'; // UEIS maps to Computer Science program
  }
  return 'Software Engineering'; // default fallback
}

// Extract credits from module code (simplified approach)
function getModuleCredits(moduleCode) {
  // Most modules are 3 credits, but some might be 4
  if (moduleCode.includes('4N') || moduleCode.includes('4')) {
    return 4;
  }
  return 3;
}

// Get semester from module code (simplified)
function getModuleSemester(moduleCode) {
  if (moduleCode.includes('1')) return 1;
  if (moduleCode.includes('2')) return 2;
  if (moduleCode.includes('3')) return 3;
  return 1; // default
}

async function importStudentData() {
  console.log('Starting student data import...');

  const studentsData = new Map(); // studentId -> student info
  const modulesData = new Map(); // moduleCode -> module info
  const resultsData = []; // array of student results

  // Parse CSV file
  return new Promise((resolve, reject) => {
    const results = [];

    fs.createReadStream('attached_assets/2025-06-06 IWD Assignment1_1755681162916.csv')
      .pipe(csv())
      .on('data', (row) => {
        // Skip header rows and empty rows
        if (!row.ID || row.ID === 'ID' || !row.Name || row.Name.includes('Grade')) {
          return;
        }

        results.push(row);
      })
      .on('end', async () => {
        console.log(`Parsed ${results.length} rows from CSV`);

        // Process each row
        for (const row of results) {
          const studentId = row.ID;
          const fullName = row.Name;
          const intake = row.Intake;
          const moduleCode = row['Subject Code'];
          const moduleName = row['Subject Name'];
          const grade = row.Grade;
          const comment = row.Comment;
          const programmeCode = row.Programme;
          const email = row.Email;

          if (!studentId || !fullName) continue;

          // Process student data
          if (!studentsData.has(studentId)) {
            const { firstName, lastName } = parseName(fullName);
            const intakeYear = parseIntakeYear(intake);
            const programme = getProgramme(programmeCode);

            studentsData.set(studentId, {
              id: studentId,
              firstName,
              lastName,
              email,
              programme,
              intakeYear,
              currentSemester: 1,
              status: 'Active'
            });
          }

          // Process module data
          if (moduleCode && moduleName && !modulesData.has(moduleCode)) {
            const credits = getModuleCredits(moduleCode);
            const semester = getModuleSemester(moduleCode);
            const programme = getProgramme(programmeCode);

            modulesData.set(moduleCode, {
              code: moduleCode,
              name: moduleName,
              credits,
              semester,
              programme,
              isCore: true
            });
          }

          // Process student result data
          if (moduleCode && grade) {
            let resultGrade = null;
            let gradePoint = null;
            let status = 'In Progress';

            // Check if it's a letter grade
            if (gradePointMap.hasOwnProperty(grade)) {
              resultGrade = grade;
              gradePoint = gradePointMap[grade];
              status = 'Completed';
            } else if (statusMap.hasOwnProperty(grade)) {
              status = statusMap[grade];
            }

            resultsData.push({
              studentId,
              moduleCode,
              semester: getModuleSemester(moduleCode),
              year: parseIntakeYear(intake),
              grade: resultGrade,
              gradePoint,
              status,
              attemptNumber: grade === '1' ? 2 : 1 // Retaking = attempt 2
            });
          }
        }

        try {
          // Insert students
          console.log(`Inserting ${studentsData.size} students...`);
          for (const studentData of studentsData.values()) {
            try {
              await db.insert(students)
                .values(studentData)
                .onConflictDoNothing();
            } catch (err) {
              console.log(`Student ${studentData.id} already exists, skipping`);
            }
          }

          // Insert modules
          console.log(`Inserting ${modulesData.size} modules...`);
          for (const moduleData of modulesData.values()) {
            try {
              await db.insert(modules)
                .values(moduleData)
                .onConflictDoNothing();
            } catch (err) {
              console.log(`Module ${moduleData.code} already exists, skipping`);
            }
          }

          // Get module IDs for results
          const moduleIdMap = new Map();
          const allModules = await db.select().from(modules);
          for (const module of allModules) {
            moduleIdMap.set(module.code, module.id);
          }

          // Insert student results
          console.log(`Inserting ${resultsData.length} student results...`);
          for (const result of resultsData) {
            const moduleId = moduleIdMap.get(result.moduleCode);
            if (!moduleId) {
              console.log(`Module not found for code: ${result.moduleCode}`);
              continue;
            }

            try {
              await db.insert(studentResults)
                .values({
                  studentId: result.studentId,
                  moduleId: moduleId,
                  semester: result.semester,
                  year: result.year,
                  grade: result.grade,
                  gradePoint: result.gradePoint ? result.gradePoint.toString() : null,
                  status: result.status,
                  attemptNumber: result.attemptNumber
                })
                .onConflictDoNothing();
            } catch (err) {
              console.log(`Error inserting result for student ${result.studentId}, module ${result.moduleCode}:`, err.message);
            }
          }

          console.log('Data import completed successfully!');
          console.log(`- ${studentsData.size} students imported`);
          console.log(`- ${modulesData.size} modules imported`);
          console.log(`- ${resultsData.length} student results imported`);

          resolve();
        } catch (error) {
          console.error('Error during import:', error);
          reject(error);
        }
      })
      .on('error', reject);
  });
}

// Run the import
importStudentData().catch(console.error);

