    for (const subject of subjectsData) {
      try {
        await this.createModule({
          code: subject.code,
          name: subject.name,
          credits: subject.credits,
          programme: subject.programme as any,
          description: `${subject.name} - ${subject.programme} Programme`
        });
        success++;
      } catch (error) {
        errors.push(`${subject.code}: ${error instanceof Error ? error.message : 'Unknown error'}`);
      }
    }

    await this.logActivity({
      action: 'BULK_IMPORT',
      entityType: 'module',
      entityId: 'bulk',
      description: `Module creation completed: ${success} modules processed, ${errors.length} errors`,
      performedBy: 'system'
    });

    return { success, errors };
  }

  async exportStudentsToCSV(): Promise<any[]> {
    const { students } = await this.getStudents();

    return students.map(student => ({
      student_id: student.id,
      first_name: student.firstName,
      last_name: student.lastName,
      email: student.email,
      programme: student.programme,
      intake_year: student.intakeYear,
      current_semester: student.currentSemester,
      status: student.status,
      current_cgpa: student.currentCGPA,
      total_credits_earned: student.totalCreditsEarned,
      remaining_credits: student.remainingCredits,
      semesters_left: student.semestersLeft,
      created_at: student.createdAt
    }));
  }
}

export const storage = new DatabaseStorage();

