async function createModules() {
  try {
    console.log('Creating modules from CSV data...');

    for (const subject of subjectsData) {
      try {
        await db.insert(modules).values({
          code: subject.code,
          name: subject.name,
          credits: subject.credits,
          programme: subject.programme,
          description: `${subject.name} - ${subject.programme} Programme`
        }).onConflictDoNothing();

        console.log(`âœ“ Created module: ${subject.code} - ${subject.name}`);
      } catch (error) {
        console.log(`âš  Module ${subject.code} may already exist: ${error.message}`);
      }
    }

    console.log(`\nâœ… Successfully processed ${subjectsData.length} modules`);
    process.exit(0);

  } catch (error) {
    console.error('Error creating modules:', error);
    process.exit(1);
  }
}

createModules();
